import React, { useRef, useState } from "react";
import styled from "styled-components";

// IMPORT COMPONENT
import Input from "../../UI/Input";

function MealItemForm(props) {
  const amountEnteredRef = useRef();
  // STATE
  const [isValid, setIsValid] = useState(true);

  // HANDLER
  const submitFormHandler = (event) => {
    event.preventDefault();
    // LOOKS AT THE AMOUNT WITHIN THE INPUT
    const enteredAmount = amountEnteredRef.current.value;
    //CONVERT AMOUNT FROM STRING TO NUMBER
    const convertAmount = +enteredAmount;

    // AMOUNT VALIDATION CHECK
    if (
      enteredAmount.trim().length === 0 ||
      convertAmount < 1 ||
      convertAmount < 5
    ) {
      setIsValid(false);
      return;
    }
  };

  return (
    <FormElement>
      <Input
        label="Amount"
        input={{
          id: "amount_" + props.id,
          type: "number",
          min: "1",
          step: "1",
          defaultValue: "1",
        }}
        onSubmit={submitFormHandler}
        ref={amountEnteredRef}
      />
      <button>+ Add</button>
      {!isValid && (
        <div>
          <p>
            Your order cannot be processed. Please check the amount you have
            entered.
          </p>
          <p>
            Make sure that the amount entered is more than 1 but less than 5
          </p>
        </div>
      )}
    </FormElement>
  );
}

export default MealItemForm;

const FormElement = styled.form`
  text-align: right;

  button {
    font: inherit;
    cursor: pointer;
    background-color: #8a2b06;
    border: 1px solid #8a2b06;
    color: white;
    padding: 0.25rem 2rem;
    border-radius: 20px;
    font-weight: bold;

    &:hover,
    &:active {
      background-color: #641e03;
      border-color: #641e03;
    }
  }
`;
