import React, { useReducer } from "react";
import CartContext from "./card-context";

// DEFAULT SNAPSHOT
const defaultCartState = {
  items: [],
  totalAmount: 0,
};

// REDUCERS
// CART REDUCER THAT GETS LAST SNASHOT IN STATE AND RETURNS NEW SNAPSHOT
// IN THIS CASE 'defaultCartState'
// ACTION WHICH WILL GET A PAYLOAD WITH DATA

const cartReducer = (state, action) => {
  if (action.type === "ADD_ITEM") {
    const updateItems = state.items.concat(action.payload);
    const updateAmount =
      state.totalAmount + action.item.price * action.payload.amount;
    return {
      items: updateItems,
      totalAmount: updateAmount,
    };
  }

  if (action.type === "REMOVE_ITEM") {
  }

  return defaultCartState;
};

function CartProvider(props) {
  const [cartState, dispatchCartAction] = useReducer(
    cartReducer,
    defaultCartState
  );

  const addItemHandler = (item) => {
    dispatchCartAction({ type: "ADD_ITEM", payload: item });
  };

  const removeItemHandler = (id) => {
    dispatchCartAction({ type: "REMOVE_ITEM", payload: id });
  };

  //HELPER
  const cardContextHelper = {
    items: cartState.items,
    totalAmount: cartState.totalAmount,
    addItem: addItemHandler,
    removeItem: removeItemHandler,
  };

  return (
    <CartContext.Provider value={cardContextHelper}>
      {props.children}
    </CartContext.Provider>
  );
}

export default CartProvider;
