import React from "react";
import styled from "styled-components";

// IMPORT COMPONENTS
import Modal from "../UI/Modal";

function Cart() {
  const cartItems = (
    <CartItemElement>
      {[{ id: "c1", name: "Sushi", amount: 2, price: 26.99 }].map((item) => (
        <li>{item.name}</li>
      ))}
    </CartItemElement>
  );
  return (
    <Modal>
      {cartItems}
      <TotalAmountContainer>
        <span>Total Amount</span>
        <span>32.99</span>
      </TotalAmountContainer>
      <ActionsContainer>
        <ActionElement>Checkout</ActionElement>
        <ActionElement>Close</ActionElement>
      </ActionsContainer>
    </Modal>
  );
}

export default Cart;

const CartItemElement = styled.ul``;

const TotalAmountContainer = styled.div``;

const ActionsContainer = styled.div`
  text-align: right;
`;

const ActionElement = styled.button``;
